import axios from "axios";

const Comments = (props) => {
  console.log(props);
  const renderComments = (item, index) => {
    return <li key={index}>{item.body}</li>;
  };
  return (
    <div>
      <ul>
        {props.comments && props.comments.length > 0 ? (
          props.comments.map((item, index) => renderComments(item, index))
        ) : (
          <div>Loading...</div>
        )}
      </ul>
    </div>
  );
};

Comments.getInitialProps = async ({ query }) => {
  const res = await axios.get(
    `https://jsonplaceholder.typicode.com/comments?postId=3`
  );
  console.log(res.data);
  return { ...query, comments: res.data };
};

export default Comments;
